mapper.select:
		SELECT U_ID as id
		FROM U
		WHERE U_NAME = #{name}
 		  AND U_EMAIL = #{email}
 		  AND DOMAIN_PART_CODE = #{domain_part_code}

		SELECT U_ID as id
		FROM U
		WHERE U_NAME = #{name}
   		  AND U_EMAIL = #{email}
 		  AND DOMAIN_PART_CODE = #{domain_part_code}
 		  AND U_ID = #{id}

mapper.update:
UPDATE U 
SET U_PW = CRYPTPACK.ENCRYPT(#{pw}, #{pw}) 
WHERE U_ID = #{id}

